import React, { useState, useEffect, useRef } from "react";
import BoardMulti from "./Board";
import ConfettiComponent from "./Confetti";
import 'bootstrap/dist/css/bootstrap.css';
import './TicTacToe.css';
import { Link, useParams } from "react-router-dom";

function TicTacToeMulti() {
  const { roomId } = useParams();
  const [turnPlayer, setTurnPlayer] = useState(1);
  const [currentPlayer, setCurrentPlayer] = useState(null);
  const [board, setBoard] = useState([
    [0, 0, 0],
    [0, 0, 0],
    [0, 0, 0],
  ]);
  const [gameOver, setGameOver] = useState(false);
  const [gameWon, setGameWon] = useState(false);
  const [winner, setWinner] = useState(0);
  const [showConfetti, setShowConfetti] = useState(false);
  const [reset, setReset] = useState(false);
  const [moves, setMoves] = useState(0);
  const [messages, setMessages] = useState([]);
  const ws = useRef(null);

  useEffect(() => {
    ws.current = new WebSocket('ws://localhost:8080');

    ws.current.onopen = () => {
      ws.current.send(JSON.stringify({ type: 'join', room: roomId }));
      setCurrentPlayer(Math.random() > 0.5 ? 1 : 2); // Assign player randomly
    };

    ws.current.onmessage = (event) => {
      const data = JSON.parse(event.data);
      switch (data.type) {
        case 'update':
          setBoard(data.board);
          setTurnPlayer(data.turnPlayer);
          break;
        case 'message':
          setMessages((prevMessages) => [...prevMessages, data.text]);
          break;
        default:
          break;
      }
    };

    return () => {
      ws.current.close();
    };
  }, [roomId]);

  const getPlayer = () => {
    return currentPlayer;
  };

  const boardMove = (space, currPlayer) => {
    if (currPlayer !== turnPlayer) {
      return; // Prevent move if it's not the player's turn
    }

    const row = Math.floor(space / 3);
    const col = space % 3;
    let newBoard = [...board];
    newBoard[row][col] = currPlayer;

    setBoard(newBoard);
    setMoves(moves + 1);

    if (checkWin(newBoard, currPlayer)) {
      setGameWon(true);
      setGameOver(true);
      setWinner(currPlayer);
      setShowConfetti(true);
    } else if (moves + 1 === 9) {
      setGameOver(true);
    } else {
      setTurnPlayer((turnPlayer % 2) + 1);
    }

    ws.current.send(JSON.stringify({ type: 'move', room: roomId, board: newBoard, turnPlayer: (turnPlayer % 2) + 1 }));
  };

  const checkWin = (board, currPlayer) => {
    for (let i = 0; i < 3; i++) {
      if (
        (board[i][0] === currPlayer && board[i][1] === currPlayer && board[i][2] === currPlayer) ||
        (board[0][i] === currPlayer && board[1][i] === currPlayer && board[2][i] === currPlayer)
      ) {
        return true;
      }
    }
    if (
      (board[0][0] === currPlayer && board[1][1] === currPlayer && board[2][2] === currPlayer) ||
      (board[0][2] === currPlayer && board[1][1] === currPlayer && board[2][0] === currPlayer)
    ) {
      return true;
    }
    return false;
  };

  const resetGame = () => {
    setTurnPlayer(1);
    setBoard([
      [0, 0, 0],
      [0, 0, 0],
      [0, 0, 0],
    ]);
    setGameWon(false);
    setWinner(0);
    setShowConfetti(false);
    setReset(!reset);
    setMoves(0);
    setGameOver(false);
  };

  const sendMessage = (text) => {
    ws.current.send(JSON.stringify({ type: 'message', room: roomId, text }));
  };

  return (
    <div className="TicTacBG">
      <h1>Multiplayer Tic Tac Toe</h1>
      {gameOver ? (
        <div>
          {gameWon ? (
            <h2>Player {winner} won!</h2>
          ) : (
            <h2>Game Over</h2>
          )}
          <button className="btn btn-success mb-2" onClick={resetGame}>Play Again</button>
        </div>
      ) : (
        <h2>Player {turnPlayer}'s turn</h2>
      )}
      <BoardMulti
        board={board}
        boardMove={boardMove}
        reset={reset}
        gameOver={gameOver}
      />
      <Link to="/"> <button className="btn btn-danger mt-2">Return</button> </Link>
      <ConfettiComponent active={showConfetti} />
      <div className="chat">
        <h3>Chat</h3>
        <div className="messages">
          {messages.map((msg, index) => (
            <p key={index}>{msg}</p>
          ))}
        </div>
        <input type="text" id="chatInput" />
        <button className="btn btn-primary" onClick={() => sendMessage(document.getElementById('chatInput').value)}>Send</button>
      </div>
    </div>
  );
}

export default TicTacToeMulti;
