import React, { useState } from "react";
import Board from "./Board";
import ConfettiComponent from "./Confetti";

function TicTacToe() {
  const [turnPlayer, setTurnPlayer] = useState(1);
  const [board, setBoard] = useState([
    [0, 0, 0],
    [0, 0, 0],
    [0, 0, 0],
  ]);
  const [gameWon, setGameWon] = useState(false);
  const [winner, setWinner] = useState(0);
  const [showConfetti, setShowConfetti] = useState(false);
  const [reset, setReset] = useState(false); // State to trigger reset

  const getPlayer = () => {
    return turnPlayer;
  };

  const boardMove = (space, currPlayer) => {
    const row = Math.floor(space / 3);
    const col = space % 3;
    let updateBoard = board;
    updateBoard[row][col] = currPlayer;
    setBoard(updateBoard);

    checkWin(currPlayer);
    setTurnPlayer((turnPlayer % 2) + 1);
  };

  const checkWin = (currPlayer) => {
    for (let i = 0; i < 3; i++) {
      if (board[i][0] !== 0) {
        if (
          (board[i][0] === board[i][1] && board[i][0] === board[i][2]) ||
          (board[0][i] === board[1][i] && board[i][0] === board[2][i])
        ) {
          setGameWon(true);
          setWinner(currPlayer);
          setShowConfetti(true);
          return;
        }
      }
    }
    if (board[0][0] !== 0) {
      if (board[0][0] === board[1][1] && board[0][0] === board[2][2]) {
        setGameWon(true);
        setWinner(currPlayer);
        setShowConfetti(true);
        return;
      }
    }
    if (board[2][0] !== 0) {
      if (board[0][2] === board[1][1] && board[0][2] === board[2][0]) {
        setGameWon(true);
        setWinner(currPlayer);
        setShowConfetti(true);
        return;
      }
    }
  };

  const resetGame = () => {
    setTurnPlayer(1);
    setBoard([
      [0, 0, 0],
      [0, 0, 0],
      [0, 0, 0],
    ]);
    setGameWon(false);
    setWinner(0);
    setShowConfetti(false);
    setReset(!reset); // Toggle reset to trigger useEffect in Square.js
  };

  return (
    <div>
      <h1>Tic Tac Toe</h1>
      {gameWon ? (
        <div>
          <h2>Player {winner} won!</h2>
          <button onClick={resetGame}>Play Again</button>
        </div>
      ) : (
        <h2>Player {turnPlayer}'s turn</h2>
      )}
      <Board getPlayer={getPlayer} boardMove={boardMove} reset={reset} />
      <ConfettiComponent active={showConfetti} />
    </div>
  );
}

export default TicTacToe;
