import React, { useState } from "react"
import Board from "./Board";
function TicTacToe () {

const [turnPlayer,setTurnPlayer]=useState(1)



const getPlayer=()=>{
    const currPlayer = turnPlayer
    setTurnPlayer((turnPlayer % 2)+1)
    return currPlayer
}



  return (
    <div>
      <h1>
        Tic Tac Toe
      </h1>
      <h2>
        Player {turnPlayer}'s turn
      </h2>
      <Board/>
    </div>
  )
};

export default TicTacToe;
