import React, { useState, useEffect, useRef } from "react";
import BoardMulti from "./BoardMulti";
import ConfettiComponent from "./Confetti";
import 'bootstrap/dist/css/bootstrap.css';
import './TicTacToe.css';
import { Link, useParams } from "react-router-dom";
import { v4 as uuidv4 } from 'uuid';
import { useNavigate } from 'react-router-dom';

function TicTacToeMulti() {
  const { roomId } = useParams();
  const [turnPlayer, setTurnPlayer] = useState(1);
  const [currentPlayer, setCurrentPlayer] = useState(null);
  const [playerId, setPlayerId] = useState(localStorage.getItem('playerId') || uuidv4());
  const [board, setBoard] = useState([
    [0, 0, 0],
    [0, 0, 0],
    [0, 0, 0],
  ]);
  const [gameOver, setGameOver] = useState(false);
  const [gameWon, setGameWon] = useState(false);
  const [winner, setWinner] = useState(0);
  const [showConfetti, setShowConfetti] = useState(false);
  const [moves, setMoves] = useState(0);
  const [messages, setMessages] = useState([]);
  const ws = useRef(null);

  const navigate = useNavigate();

  // WebSocket setup
  useEffect(() => {
    ws.current = new WebSocket('ws://localhost:8080');

    ws.current.onopen = () => {
      ws.current.send(JSON.stringify({ type: 'join', room: roomId, playerId }));
    };

    ws.current.onmessage = (event) => {
      const data = JSON.parse(event.data);
      console.log(data);
      switch (data.type) {
        case 'playerNumber':
          if (data.players > 2) {
            alert('The game already has two players.');
            ws.current.close();
            navigate('/'); // Redirect any third player out of the game
          } else {
            setCurrentPlayer(data.playerNumber);
            setPlayerId(data.playerId);
            localStorage.setItem('playerId', data.playerId);
          }
          break;
        case 'start':
          console.log(data.message);
          break;
        case 'update':
          setBoard(data.board);
          setTurnPlayer(data.turnPlayer);
          break;
        case 'gameOver':
          setBoard(data.board);
          setGameOver(true);
          setWinner(data.winner);
          setGameWon(data.winner !== 0);
          setShowConfetti(data.winner === currentPlayer);
          break;
        case 'message':
          setMessages((prevMessages) => [...prevMessages, data.text]);
          break;
        case 'reset':
          resetLocalGameState();
          break;
        case 'error':
          alert(data.message);
          navigate('/');
          break;
        case 'syncState':
          setBoard(data.board);
          setTurnPlayer(data.turnPlayer);
          break;
        default:
          break;
      }
    };

    return () => {
      ws.current.close();
    };
  }, [roomId, playerId]);

  // Get the current player number
  const getPlayer = () => {
    return currentPlayer;
  };

  // Handle a move on the board
  const boardMove = (space) => {
    const currPlayer = getPlayer();
    if (currPlayer == null || currPlayer !== turnPlayer || gameOver) {
      console.log('Move prevented: either game is over or not the player\'s turn');
      return;
    }

    const row = Math.floor(space / 3);
    const col = space % 3;
    let newBoard = [...board];
    if (newBoard[row][col] === 0) {  // Only allow a move in an empty space
      newBoard[row][col] = currPlayer;

      setBoard(newBoard);
      setMoves(moves + 1);
      setTurnPlayer((turnPlayer % 2) + 1);

      // Send the move to the server
      ws.current.send(JSON.stringify({
        type: 'move',
        room: roomId,
        board: newBoard,
        turnPlayer: (turnPlayer % 2) + 1,  // Update turn for the next player
        moveCnt: moves + 1
      }));
    }
  };

  // Reset the game state and notify the server
  const resetGame = () => {
    ws.current.send(JSON.stringify({
      type: 'playAgain',
      room: roomId,
      playerId: playerId,
    }));
  };

  // Send a message in the chat
  const sendMessage = (playerMessage) => {
    const text = 'Player ' + currentPlayer + ': ' + playerMessage;
    ws.current.send(JSON.stringify({ type: 'message', room: roomId, text }));
  };

  // Request to switch player numbers
  const requestSwitchPlayers = () => {
    ws.current.send(JSON.stringify({
      type: 'switchRequest',
      room: roomId,
      playerId: playerId,
    }));
  };

  // Reset the local game state
  const resetLocalGameState = () => {
    setTurnPlayer(1);
    setBoard([
      [0, 0, 0],
      [0, 0, 0],
      [0, 0, 0],
    ]);
    setGameWon(false);
    setWinner(0);
    setShowConfetti(false);
    setMoves(0);
    setGameOver(false);
  };

  return (
    <div className="TicTacBG">
      <h1>Welcome To The Game Room Player {currentPlayer}</h1>
      {gameOver ? (
        <div>
          {gameWon ? (
            <h2>Player {winner} won!</h2>
          ) : (
            <h2>It's a tie!</h2>
          )}
          <button className="btn btn-success mb-2" onClick={resetGame}>Play Again</button>
        </div>
      ) : (
        <h2>Player {turnPlayer}'s turn</h2>
      )}
      <BoardMulti
        board={board}
        boardMove={boardMove}
        gameOver={gameOver}
      />
      <Link to="/"> <button className="btn btn-danger mt-2">Return</button> </Link>
      <button className="btn btn-secondary mt-2" onClick={requestSwitchPlayers}>Switch Player Number</button>
      <ConfettiComponent active={showConfetti} />
      <div className="chat">
        <h3>Chat</h3>
        <div className="messages">
          {messages.map((msg, index) => (
            <p key={index}>{msg}</p>
          ))}
        </div>
        <input type="text" id="chatInput" />
        <button className="btn btn-primary" onClick={() => sendMessage(document.getElementById('chatInput').value)}>Send</button>
      </div>
    </div>
  );
}

export default TicTacToeMulti;
